import React, { useState, useEffect } from 'react';
import { 
  fetchAvailableSeats, 
  bookSlot, 
  fetchBookedSlots, 
  deleteBookedSlot, 
  checkApiHealth, 
  API_BASE_URL,
  fetchPendingBookings,
  handleBookingAction,
  fetchDayOrder
} from '../services/api';
import './SlotBooking.css';
import { supabase } from '../supabaseClient';
import TimeSlots from '../components/TimeSlots';

const SlotBooking = () => {
  // Form state
  const [bookingForm, setBookingForm] = useState({
    facultyId: '',
    name: '',
    email: '',
    department: '',
    date: '',
    dayOrder: '',
    lab: '',
    timeSlots: [] // Changed from timeSlot to timeSlots array
  });

  // UI state
  const [timeSlots, setTimeSlots] = useState([]);
  const [loading, setLoading] = useState(false);
  const [bookedSlots, setBookedSlots] = useState([]);
  const [showBookedSlots, setShowBookedSlots] = useState(true); // Set to true by default
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [apiError, setApiError] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedSlots, setSelectedSlots] = useState([]); // Changed from selectedSlot to selectedSlots array
  const [message, setMessage] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [pendingBookings, setPendingBookings] = useState([]);
  const [user, setUser] = useState(null);

  // Check API health and initialize user on component mount
  useEffect(() => {
    const checkServerHealth = async () => {
      const isHealthy = await checkApiHealth();
      setApiError(!isHealthy);
    };

    const initializeUser = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (user) {
          setUser(user);
          const userIsAdmin = isAdminUser(user.email);
          setIsAdmin(userIsAdmin);

          setBookingForm(prev => ({
            ...prev,
            email: user.email,
            name: user.user_metadata?.full_name || user.email
          }));

          if (user.email) {
            await fetchUserBookings(user.email);
            if (userIsAdmin) {
              await fetchPendingBookingsData();
            }
          }
        }
      } catch (error) {
        console.error('Error initializing user:', error);
      }
    };

    checkServerHealth();
    initializeUser();

    // Set the minimum date to today
    const today = new Date().toISOString().split("T")[0];
    const dateInput = document.getElementById("date");
    if (dateInput) {
      dateInput.setAttribute("min", today);
    }
  }, []);

  // Function to check if a user is admin
  const isAdminUser = (email) => {
    const adminEmails = ['km5260@srmist.edu.in'];
    return adminEmails.includes(email);
  };

  // Utility function to check if a date is a weekend
  const isWeekend = (date) => {
    const day = new Date(date).getDay();
    // 0 is Sunday, 6 is Saturday
    return day === 0 || day === 6;
  };

  // Function to provide a descriptive date string
  const formatDateForDisplay = (dateString) => {
    const date = new Date(dateString);
    const day = date.getDay();
    const isWeekendDay = day === 0 || day === 6;
    const formattedDate = date.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });

    return `${formattedDate}${isWeekendDay ? ' (Weekend - Not Available)' : ''}`;
  };

  // Function to check if a date should be disabled (weekends)
  const isDateDisabled = (date) => {
    const currentDate = new Date(date);
    return currentDate.getDay() === 0 || currentDate.getDay() === 6; // 0 is Sunday, 6 is Saturday
  };

  // Function to get the next available date (skip weekends)
  const getNextAvailableDate = (date) => {
    const currentDate = new Date(date);
    while (isDateDisabled(currentDate)) {
      currentDate.setDate(currentDate.getDate() + 1);
    }
    return currentDate.toISOString().split('T')[0];
  };

  // Handle booking form input changes
  const handleBookingChange = async (e) => {
    const { name, value } = e.target;

    // Prevent only email from being changed
    if (name === 'email') {
      return;
    }

    // Clear previous messages
    setError('');
    setSuccess('');

    // Handle date changes with weekend validation
    if (name === 'date' && value) {
      if (isDateDisabled(value)) {
        const nextAvailableDate = getNextAvailableDate(value);
        setError('Weekends are not available for booking. Next available date selected.');

        // Update the date input to the next available date
        e.target.value = nextAvailableDate;

        try {
          setLoading(true);
          // Get day order for next available date
          const response = await fetch(`${API_BASE_URL}/day-order?date=${encodeURIComponent(nextAvailableDate)}`);
          const data = await response.json();

          if (response.ok && data.day_order) {
            setBookingForm(prev => ({
              ...prev,
              date: nextAvailableDate,
              dayOrder: data.day_order.toString(),
              lab: '',
              timeSlots: []
            }));
            setTimeSlots([]);
            setSelectedSlots([]);
          }
        } catch (error) {
          console.error('Error fetching day order:', error);
          setError('Error fetching day order. Please try again.');
        } finally {
          setLoading(false);
        }
        return;
      }

      try {
        setLoading(true);
        // Get day order for selected date
        const response = await fetch(`${API_BASE_URL}/day-order?date=${encodeURIComponent(value)}`);
        const data = await response.json();

        if (response.ok && data.day_order) {
          setBookingForm(prev => ({
            ...prev,
            date: value,
            dayOrder: data.day_order.toString(),
            lab: '',  // Reset lab selection when date changes
            timeSlots: []  // Reset time slots when date changes
          }));
          setTimeSlots([]); // Clear time slots
          setSelectedSlots([]); // Clear selected slots when date changes
        } else {
          setError('Invalid date selected. Please choose a weekday.');
          setBookingForm(prev => ({
            ...prev,
            dayOrder: '',
            lab: '',  // Reset lab selection on error
            timeSlots: []  // Reset time slots on error
          }));
          setSelectedSlots([]); // Clear selected slots on error
        }
      } catch (error) {
        console.error('Error fetching day order:', error);
        setError('Error fetching day order. Please try again.');
      } finally {
        setLoading(false);
      }
    } else {
      // Update form state for other fields
      setBookingForm(prev => ({
        ...prev,
        [name]: value
      }));

      // Handle lab selection
      if (name === 'lab' && value) {
        setSelectedSlots([]); // Clear selected slots when lab changes
        if (bookingForm.date) {
          handleFetchAvailableSeats(bookingForm.date, value);
        }
      }
    }
  };

  // Retry server connection
  const handleRetryConnection = async () => {
    setLoading(true);
    const isHealthy = await checkApiHealth();
    setApiError(!isHealthy);
    setLoading(false);

    if (isHealthy && bookingForm.date && bookingForm.lab) {
      handleFetchAvailableSeats(bookingForm.date, bookingForm.lab);
    }
  };

  // Fetch available seats for selected date and lab
  const handleFetchAvailableSeats = async (selectedDate, selectedLab) => {
    if (!selectedDate || !selectedLab) {
      setTimeSlots([]);
      return;
    }

    setLoading(true);
    setError('');
    setApiError(false);

    try {
      const data = await fetchAvailableSeats(selectedDate, selectedLab);
      console.log('Available slots response:', data);

      // Check if the slot should be blocked based on Lab D regular classes schedule
      const isSlotBlockedForLabD = (timeSlot, dayOrder) => {
        if (selectedLab !== 'LAB D' && selectedLab !== 'LAB-D') return false;

        // Time slots to block for Lab D by day order
        const blockedSlots = {
          '1': ['02:20-03:10', '03:10-04:00'],                     // Day 1: 2:10 PM to 4:00 PM
          '2': ['01:25-02:15', '02:20-03:10', '03:10-04:00', '04:00-04:50'],  // Day 2: 1:25 PM to 4:55 PM
          '3': ['09:45-10:35', '10:40-11:30'],                     // Day 3: 9:45 AM to 11:30 AM
          '4': [],                                                 // Day 4: NIL (no class)
          '5': ['08:00-08:50', '08:50-09:40', '09:45-10:35', '10:40-11:30']   // Day 5: 8:00 AM to 11:30 AM
        };

        return blockedSlots[dayOrder]?.includes(timeSlot) || false;
      };

      // Transform data for the time slot dropdown with boolean availability
      const availableTimeSlots = [
        { value: "08:00-08:50", label: "8:00 AM - 8:50 AM", available: data["08:00-08:50"] === true && !isSlotBlockedForLabD("08:00-08:50", bookingForm.dayOrder) },
        { value: "08:50-09:40", label: "8:50 AM - 9:40 AM", available: data["08:50-09:40"] === true && !isSlotBlockedForLabD("08:50-09:40", bookingForm.dayOrder) },
        { value: "09:45-10:35", label: "9:45 AM - 10:35 AM", available: data["09:45-10:35"] === true && !isSlotBlockedForLabD("09:45-10:35", bookingForm.dayOrder) },
        { value: "10:40-11:30", label: "10:40 AM - 11:30 AM", available: data["10:40-11:30"] === true && !isSlotBlockedForLabD("10:40-11:30", bookingForm.dayOrder) },
        { value: "11:35-12:25", label: "11:35 AM - 12:25 PM", available: data["11:35-12:25"] === true && !isSlotBlockedForLabD("11:35-12:25", bookingForm.dayOrder) },
        { value: "12:30-01:20", label: "12:30 PM - 1:20 PM", available: data["12:30-01:20"] === true && !isSlotBlockedForLabD("12:30-01:20", bookingForm.dayOrder) },
        { value: "01:25-02:15", label: "1:25 PM - 2:15 PM", available: data["01:25-02:15"] === true && !isSlotBlockedForLabD("01:25-02:15", bookingForm.dayOrder) },
        { value: "02:20-03:10", label: "2:20 PM - 3:10 PM", available: data["02:20-03:10"] === true && !isSlotBlockedForLabD("02:20-03:10", bookingForm.dayOrder) },
        { value: "03:10-04:00", label: "3:10 PM - 4:00 PM", available: data["03:10-04:00"] === true && !isSlotBlockedForLabD("03:10-04:00", bookingForm.dayOrder) },
        { value: "04:00-04:50", label: "4:00 PM - 4:50 PM", available: data["04:00-04:50"] === true && !isSlotBlockedForLabD("04:00-04:50", bookingForm.dayOrder) }
      ];

      setTimeSlots(availableTimeSlots);
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle booking form submission
  const handleBookingSubmit = async (e) => {
    e.preventDefault();

    // Clear previous messages
    setError('');
    setSuccess('');

    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        setError('Please sign in to book slots.');
        return;
      }

      // Validate form inputs
      if (!bookingForm.facultyId || 
          !bookingForm.name || 
          !bookingForm.email || 
          !bookingForm.department ||
          !bookingForm.date || 
          !bookingForm.lab || 
          !selectedSlots.length) {
        setError("Please fill out all fields before submitting.");
        return;
      }

      // Validate SRM email
      if (!bookingForm.email.toLowerCase().endsWith('@srmist.edu.in')) {
        setError("Please use an email ending with @srmist.edu.in");
        return;
      }

      // Track successful and failed bookings
      const successfulBookings = [];
      const failedBookings = [];

      // Book each selected slot
      for (const slot of selectedSlots) {
        const bookingData = {
          date: bookingForm.date,
          timeSlot: slot,
          email: user.email,
          facultyId: bookingForm.facultyId,
          name: bookingForm.name,
          department: bookingForm.department,
          lab: bookingForm.lab
        };

        try {
          const response = await fetch(`${API_BASE_URL}/book-slot`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(bookingData),
          });

          const result = await response.json();

          if (response.ok && result.success) {
            successfulBookings.push(slot);
          } else {
            failedBookings.push({ 
              slot, 
              error: result.error,
              lab: bookingForm.lab 
            });
            // If we need to refresh available slots
            if (result.should_refresh) {
              // Wait a short moment before refreshing to avoid race conditions
              setTimeout(() => {
                handleFetchAvailableSeats(bookingForm.date, bookingForm.lab);
              }, 500);
            }
          }
        } catch (error) {
          failedBookings.push({ 
            slot, 
            error: error.message,
            lab: bookingForm.lab 
          });
        }
      }

      // Update UI based on results
      if (successfulBookings.length > 0) {
        setSuccess(`Successfully booked ${successfulBookings.length} slot(s) in ${bookingForm.lab}: ${successfulBookings.join(', ')}`);

        // Refresh booked slots
        await fetchUserBookings(user.email);

        // Remove successful bookings from selected slots
        setSelectedSlots(prevSlots => 
          prevSlots.filter(slot => !successfulBookings.includes(slot))
        );

        // Clear form only if all slots were booked successfully
        if (failedBookings.length === 0) {
          setBookingForm(prev => ({
            ...prev,
            facultyId: '',
            name: '',
            department: '',
            date: '',
            dayOrder: '',
            lab: '',
            timeSlots: []
          }));
          setSelectedSlots([]);
          setTimeSlots([]);
        }
      }

      // Show error for failed bookings
      if (failedBookings.length > 0) {
        const errorMessages = failedBookings.map(({ slot, error, lab }) => 
          `Failed to book ${slot} in ${lab}: ${error}`
        );
        setError(errorMessages.join('\n'));
      }

    } catch (error) {
      console.error('Submission error:', error);
      setError(error.message || 'Error submitting booking. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Fetch booked slots for a user
  const fetchUserBookings = async (email) => {
    if (!email || !email.toLowerCase().endsWith('@srmist.edu.in')) {
      return;
    }

    setLoading(true);

    try {
      const data = await fetchBookedSlots(email);
      setBookedSlots(data);
      setShowBookedSlots(true);

      if (data.length === 0) {
        setSuccess("No booked slots found for your email.");
      }
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        console.error('Error fetching bookings:', error);
      }
    } finally {
      setLoading(false);
    }
  };

  // Delete a booked slot
  const handleDeleteBookedSlot = async (bookingId) => {
    if (!window.confirm("Are you sure you want to delete this slot?")) {
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const data = await deleteBookedSlot(bookingId);
      setSuccess(data.message);

      // Refresh the booked slots using the email from the form
      if (bookingForm.email) {
        fetchUserBookings(bookingForm.email);
      }

      // Fetch available seats again to update the slots
      if (bookingForm.date && bookingForm.lab) {
        handleFetchAvailableSeats(bookingForm.date, bookingForm.lab);
      }

    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSlotSelect = (slot) => {
    setSelectedSlots(prevSlots => {
      const newSlots = prevSlots.includes(slot)
        ? prevSlots.filter(s => s !== slot)  // Remove slot if already selected
        : [...prevSlots, slot];              // Add slot if not selected

      // Update bookingForm.timeSlots as well
      setBookingForm(prev => ({
        ...prev,
        timeSlots: newSlots
      }));

      return newSlots;
    });
    setError('');
    setSuccess('');
  };

  // Function to fetch pending bookings (admin only)
  const fetchPendingBookingsData = async () => {
    try {
      if (!user?.email) return;

      const response = await fetch(`${API_BASE_URL}/admin/pending-bookings?admin_email=${encodeURIComponent(user.email)}`);
      if (!response.ok) throw new Error('Failed to fetch pending bookings');

      const data = await response.json();
      setPendingBookings(data);
    } catch (error) {
      console.error('Error fetching pending bookings:', error);
      setError('Failed to fetch pending bookings');
    }
  };

  // Function to handle booking status change (confirm/reject)
  const handleBookingStatusChange = async (bookingId, action) => {
    try {
      if (!user?.email) return;

      const response = await fetch(`${API_BASE_URL}/admin/booking/${bookingId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          admin_email: user.email,
          action: action
        })
      });

      if (!response.ok) throw new Error(`Failed to ${action} booking`);

      // Refresh pending bookings list
      await fetchPendingBookingsData();
      setSuccess(`Booking ${action}ed successfully`);
    } catch (error) {
      console.error(`Error ${action}ing booking:`, error);
      setError(`Failed to ${action} booking`);
    }
  };

  return (
    <div className="slot-booking-container">
      <h2>Lab Slot Booking</h2>

      <form onSubmit={handleBookingSubmit} className="booking-form">
        <label htmlFor="facultyId">Faculty ID:</label>
        <input 
          type="text" 
          id="facultyId" 
          name="facultyId" 
          value={bookingForm.facultyId}
          onChange={handleBookingChange}
          required 
          placeholder="Enter your faculty ID"
          disabled={loading || apiError}
        />

        <label htmlFor="name">Full Name:</label>
        <input 
          type="text" 
          id="name" 
          name="name" 
          value={bookingForm.name}
          onChange={handleBookingChange}
          required
          placeholder="Enter your full name"
        />

        <label htmlFor="email">Email (SRM):</label>
        <input 
          type="email" 
          id="email" 
          name="email" 
          value={bookingForm.email}
          readOnly
          disabled
          className="readonly-input"
        />

        <label htmlFor="department">Department:</label>
        <select 
          id="department" 
          name="department" 
          value={bookingForm.department}
          onChange={handleBookingChange}
          required
          disabled={loading || apiError}
        >
          <option value="">Select Department</option>
          <option value="NETWORKING">Networking</option>
          <option value="CINTEL">CINTEL</option>
          <option value="DSBS">DSBS</option>
          <option value="CTECH">CTECH</option>
        </select>

        <div className="form-group">
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={bookingForm.date}
            onChange={handleBookingChange}
            required
            disabled={loading || apiError}
            min={new Date().toISOString().split('T')[0]}
            onKeyDown={(e) => e.preventDefault()} // Prevent manual input
          />
        </div>

        <div className="form-group">
          <label htmlFor="dayOrder">Day Order:</label>
          <input
            type="text"
            id="dayOrder"
            name="dayOrder"
            value={bookingForm.dayOrder ? `Day ${bookingForm.dayOrder}` : ''}
            readOnly
            className="readonly-input"
            style={{ backgroundColor: '#f5f5f5', padding: '8px', border: '1px solid #ddd' }}
            placeholder="Select a date to see day order"
          />
        </div>

        <label htmlFor="lab">Select Lab:</label>
        <select 
          id="lab" 
          name="lab" 
          value={bookingForm.lab}
          onChange={handleBookingChange}
          required
          disabled={loading || apiError}
          className="lab-select"
        >
          <option value="">Select a Lab</option>
          <option value="LAB A">LAB A</option>
          <option value="LAB B">LAB B</option>
          <option value="LAB C">LAB C</option>
          <option value="LAB D">LAB D</option>
        </select>

        <div className="time-slots-grid">
          {loading && timeSlots.length === 0 ? (
            <div>Loading available slots...</div>
          ) : timeSlots.length > 0 ? (
            timeSlots.map(slot => (
              <div
                key={slot.value}
                className={`time-slot-item ${!slot.available ? 'disabled' : ''} ${selectedSlots.includes(slot.value) ? 'selected' : ''}`}
                onClick={() => slot.available && handleSlotSelect(slot.value)}
              >
                <div className="time-slot-time">{slot.label}</div>
                <div className="time-slot-status">
                  {slot.available ? 'Available' : 'Booked'}
                </div>
              </div>
            ))
          ) : (
            <div>Select a date and lab first</div>
          )}
        </div>

        <div className="selected-slots">
          <h3>Selected Time Slots:</h3>
          {selectedSlots.length > 0 ? (
            <div className="selected-slots-list">
              {selectedSlots.map(slot => (
                <div key={slot} className="selected-slot-item">
                  {slot}
                  <button 
                    className="remove-slot"
                    onClick={() => handleSlotSelect(slot)}
                  >

                  </button>
                </div>
              ))}
            </div>
          ) : (
            <p>No slots selected</p>
          )}
        </div>

        <div className="button-container">
          <button 
            type="submit"
            className="booking-button"
            disabled={loading || apiError || selectedSlots.length === 0}
          >
            {loading ? 'Booking...' : `Book ${selectedSlots.length} Slot${selectedSlots.length !== 1 ? 's' : ''}`}
          </button>
        </div>
      </form>

      {apiError && (
        <div className="api-error">
          <h3>Connection Error</h3>
          <p>Cannot connect to the backend server. Please make sure the Flask server is running on port 8080.</p>
          <button 
            onClick={handleRetryConnection} 
            disabled={loading}
            className="action-button"
          >
            {loading ? 'Trying to connect...' : 'Retry Connection'}
          </button>
        </div>
      )}

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {success && (
        <div className="success-message">
          {success}
        </div>
      )}

      {message && (
        <div className={`message ${message.includes('Error') || message.includes('Please') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      {showBookedSlots && bookedSlots.length > 0 && (
        <div className="booked-slots-list">
          {bookedSlots.map(booking => (
            <div key={booking.id} className="booked-slot-item">
              <div className="booked-slot-details">
                <p><strong>Date:</strong> {booking.date}</p>
                <p><strong>Time Slot:</strong> {booking.time_slot}</p>
                <p><strong>Day Order:</strong> {booking.day_order}</p>
                <p><strong>Lab:</strong> {booking.lab}</p>
                <p><strong>Status:</strong> <span className={`status-${booking.status?.toLowerCase() || 'waiting'}`}>
                  {booking.status?.charAt(0).toUpperCase() + booking.status?.slice(1) || 'Waiting'}
                </span></p>
              </div>
              {(booking.status === 'waiting' || booking.status === 'confirmed') && (
                <button
                  onClick={() => handleDeleteBookedSlot(booking.id)}
                  className="delete-button"
                  disabled={loading}
                >
                  Cancel Booking
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SlotBooking;
