const API_BASE_URL = 'http://localhost:8080/api';

/**
 * Check if the API server is running
 * @returns {Promise<boolean>} True if the API is healthy, false otherwise
 */
export async function checkApiHealth() {
  try {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 5000);
    
    const response = await fetch(`${API_BASE_URL}/health`, {
      signal: controller.signal
    });
    
    clearTimeout(timeoutId);
    
    // Check if response is JSON
    const contentType = response.headers.get("content-type");
    if (!contentType || !contentType.includes("application/json")) {
      console.error('Health check returned non-JSON response');
      return false;
    }
    
    const data = await response.json();
    return response.ok && data.status === 'ok';
  } catch (error) {
    console.error('Health check failed:', error);
    return false;
  }
}

/**
 * Helper function to handle API responses
 * @param {Response} response - The fetch response object
 * @returns {Promise<any>} - Parsed JSON response
 */
async function handleResponse(response) {
  const contentType = response.headers.get("content-type");
  if (!contentType || !contentType.includes("application/json")) {
    const text = await response.text();
    console.error('Non-JSON response:', text);
    throw new Error('Backend server error. Please make sure the server is running.');
  }
  
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.error || data.message || 'API request failed');
  }
  return data;
}

/**
 * Fetch available seats for a given date
 * @param {string} date - The date to fetch available seats for
 * @returns {Promise<Object>} - Available seats data
 */
export async function fetchAvailableSeats(date) {
  try {
    // Check API health first
    const isHealthy = await checkApiHealth();
    if (!isHealthy) {
      throw new Error('Backend server is not running. Please start the Flask server.');
    }

    const response = await fetch(`${API_BASE_URL}/available-seats?date=${date}`);
    return handleResponse(response);
  } catch (error) {
    console.error('Error fetching available seats:', error);
    throw error;
  }
}

/**
 * Book a lab slot
 * @param {Object} bookingData - The booking data
 * @returns {Promise<Object>} - Booking confirmation
 */
export async function bookSlot(bookingData) {
  try {
    // Check API health first
    const isHealthy = await checkApiHealth();
    if (!isHealthy) {
      throw new Error('Backend server is not running. Please start the Flask server.');
    }

    const response = await fetch(`${API_BASE_URL}/book-slot`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(bookingData),
    });

    return handleResponse(response);
  } catch (error) {
    console.error('Error booking slot:', error);
    throw error;
  }
}

/**
 * Fetch booked slots for a user
 * @param {string} email - The user's email
 * @returns {Promise<Array>} - Array of booked slots
 */
export async function fetchBookedSlots(email) {
  try {
    // Check API health first
    const isHealthy = await checkApiHealth();
    if (!isHealthy) {
      throw new Error('Backend server is not running. Please start the Flask server.');
    }

    const response = await fetch(`${API_BASE_URL}/booked-slots?email=${encodeURIComponent(email)}`);
    return handleResponse(response);
  } catch (error) {
    console.error('Error fetching booked slots:', error);
    throw error;
  }
}

/**
 * Delete a booked slot
 * @param {number} slotId - The slot ID to delete
 * @returns {Promise<Object>} - Deletion confirmation
 */
export async function deleteBookedSlot(slotId) {
  try {
    // Check API health first
    const isHealthy = await checkApiHealth();
    if (!isHealthy) {
      throw new Error('Backend server is not running. Please start the Flask server.');
    }

    const response = await fetch(`${API_BASE_URL}/delete-slot/${slotId}`, {
      method: 'DELETE',
    });

    return handleResponse(response);
  } catch (error) {
    console.error('Error deleting booked slot:', error);
    throw error;
  }
}
