import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_BASE_URL } from '../services/api';
import { supabase } from '../supabaseClient';
import './PendingBookings.css';

const PendingBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [selectedStatus, setSelectedStatus] = useState('waiting');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [counts, setCounts] = useState({ waiting: 0, confirmed: 0, rejected: 0 });
  const navigate = useNavigate();

  useEffect(() => {
    checkAdminAndFetchBookings();
  }, []);

  const checkAdminAndFetchBookings = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        navigate('/login');
        return;
      }

      const adminEmails = ['km5260@srmist.edu.in', 'manorant@srmist.edu.in'];
      if (!adminEmails.includes(user.email)) {
        navigate('/');
        return;
      }

      await fetchAllBookings(user.email);
    } catch (error) {
      console.error('Error:', error);
      setError('Failed to authenticate');
    }
  };

  const fetchAllBookings = async (adminEmail) => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/booked-slots?email=${encodeURIComponent(adminEmail)}`);
      if (!response.ok) throw new Error('Failed to fetch bookings');
      
      const data = await response.json();
      
      // Update the counts
      setCounts({
        waiting: data.counts.waiting,
        confirmed: data.counts.confirmed,
        rejected: data.counts.rejected
      });
      
      // Set initial bookings based on selected status
      setBookings(data.bookings[selectedStatus] || []);
    } catch (error) {
      console.error('Error fetching bookings:', error);
      setError('Failed to fetch bookings');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (status) => {
    setSelectedStatus(status);
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();
      const response = await fetch(`${API_BASE_URL}/booked-slots?email=${encodeURIComponent(user.email)}&status=${status}`);
      if (!response.ok) throw new Error('Failed to fetch bookings');
      
      const data = await response.json();
      setBookings(data.bookings || []);
    } catch (error) {
      console.error('Error fetching bookings:', error);
      setError('Failed to fetch bookings');
    } finally {
      setLoading(false);
    }
  };

  const handleBookingAction = async (bookingId, action) => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/admin/booking/${bookingId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          admin_email: user.email,
          action: action
        })
      });

      if (!response.ok) throw new Error(`Failed to ${action} booking`);

      // Refresh all bookings to update counts and current view
      await fetchAllBookings(user.email);
      setSuccess(`Booking ${action}ed successfully`);
      
      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(null), 3000);
    } catch (error) {
      console.error(`Error ${action}ing booking:`, error);
      setError(`Failed to ${action} booking`);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="pending-bookings-page">
      <h2>Lab Bookings</h2>
      {success && <div className="success-message">{success}</div>}
      
      <div className="status-filter-buttons">
        <button
          className={`status-button ${selectedStatus === 'waiting' ? 'active' : ''}`}
          onClick={() => handleStatusChange('waiting')}
        >
          Waiting ({counts.waiting})
        </button>
        <button
          className={`status-button ${selectedStatus === 'confirmed' ? 'active' : ''}`}
          onClick={() => handleStatusChange('confirmed')}
        >
          Confirmed ({counts.confirmed})
        </button>
        <button
          className={`status-button ${selectedStatus === 'rejected' ? 'active' : ''}`}
          onClick={() => handleStatusChange('rejected')}
        >
          Rejected ({counts.rejected})
        </button>
      </div>
      
      {bookings.length > 0 ? (
        <div className="bookings-list">
          {bookings.map(booking => (
            <div key={booking.id} className="booking-card">
              <div className="booking-info">
                <div className="info-group">
                  <h3>User Details</h3>
                  <p><strong>Name:</strong> {booking.name}</p>
                  <p><strong>Email:</strong> {booking.email}</p>
                  <p><strong>Faculty ID:</strong> {booking.faculty_id}</p>
                  <p><strong>Department:</strong> {booking.department}</p>
                </div>
                <div className="info-group">
                  <h3>Booking Details</h3>
                  <p><strong>Date:</strong> {booking.formatted_date || booking.date}</p>
                  <p><strong>Day Order:</strong> {booking.day_order}</p>
                  <p><strong>Lab:</strong> {booking.lab}</p>
                  <p><strong>Time Slot:</strong> {booking.time_slot}</p>
                </div>
              </div>
              {selectedStatus === 'waiting' && (
                <div className="action-buttons">
                  <button
                    onClick={() => handleBookingAction(booking.id, 'confirm')}
                    className="confirm-button"
                    disabled={loading}
                  >
                    Confirm
                  </button>
                  <button
                    onClick={() => handleBookingAction(booking.id, 'reject')}
                    className="reject-button"
                    disabled={loading}
                  >
                    Reject
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <div className="no-bookings">No {selectedStatus} bookings available</div>
      )}
    </div>
  );
};

export default PendingBookings;
