import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import './PendingBookings.css';

const PendingBookings = () => {
  const [pendingBookings, setPendingBookings] = useState([]);
  const [bookingHistory, setBookingHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activeTab, setActiveTab] = useState('pending'); // 'pending' or 'history'

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();
      
      if (!user) {
        setError('Please log in to view bookings');
        return;
      }

      // Fetch pending bookings
      const pendingResponse = await fetch(`/api/admin/pending-bookings?admin_email=${encodeURIComponent(user.email)}`);
      const pendingData = await pendingResponse.json();
      setPendingBookings(pendingData);

      // Fetch booking history (accepted/rejected)
      const historyResponse = await fetch(`/api/admin/booking-history?admin_email=${encodeURIComponent(user.email)}`);
      const historyData = await historyResponse.json();
      setBookingHistory(historyData);

    } catch (error) {
      console.error('Error fetching bookings:', error);
      setError('Failed to fetch bookings');
    } finally {
      setLoading(false);
    }
  };

  const handleBookingAction = async (bookingId, action) => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();
      
      if (!user) {
        setError('Please log in to perform this action');
        return;
      }

      const response = await fetch(`/api/admin/booking/${bookingId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          admin_email: user.email,
          action: action
        })
      });

      if (!response.ok) {
        throw new Error(`Failed to ${action} booking`);
      }

      setSuccess(`Booking ${action}ed successfully`);
      fetchBookings(); // Refresh both pending and history lists
    } catch (error) {
      console.error(`Error ${action}ing booking:`, error);
      setError(`Failed to ${action} booking`);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="pending-bookings-page">
      <h2>Booking Management</h2>

      <div className="tabs">
        <button 
          className={`tab-button ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Bookings
        </button>
        <button 
          className={`tab-button ${activeTab === 'history' ? 'active' : ''}`}
          onClick={() => setActiveTab('history')}
        >
          Booking History
        </button>
      </div>

      {loading && <div className="loading">Loading bookings...</div>}
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      {activeTab === 'pending' && (
        <div className="bookings-section">
          <h3>Pending Bookings</h3>
          {pendingBookings.length === 0 ? (
            <p className="no-bookings">No pending bookings</p>
          ) : (
            <div className="bookings-list">
              {pendingBookings.map(booking => (
                <div key={booking.id} className="booking-card">
                  <div className="booking-info">
                    <h4>{booking.name}</h4>
                    <p><strong>Email:</strong> {booking.email}</p>
                    <p><strong>Department:</strong> {booking.department}</p>
                    <p><strong>Date:</strong> {formatDate(booking.date)}</p>
                    <p><strong>Time:</strong> {booking.time_slot}</p>
                    <p><strong>Lab:</strong> {booking.lab}</p>
                    <p><strong>Purpose:</strong> {booking.purpose}</p>
                  </div>
                  <div className="action-buttons">
                    <button
                      onClick={() => handleBookingAction(booking.id, 'confirm')}
                      className="confirm-button"
                      disabled={loading}
                    >
                      Confirm
                    </button>
                    <button
                      onClick={() => handleBookingAction(booking.id, 'reject')}
                      className="reject-button"
                      disabled={loading}
                    >
                      Reject
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {activeTab === 'history' && (
        <div className="bookings-section">
          <h3>Booking History</h3>
          {bookingHistory.length === 0 ? (
            <p className="no-bookings">No booking history</p>
          ) : (
            <div className="bookings-list">
              {bookingHistory.map(booking => (
                <div key={booking.id} className="booking-card">
                  <div className="booking-info">
                    <h4>{booking.name}</h4>
                    <p><strong>Email:</strong> {booking.email}</p>
                    <p><strong>Department:</strong> {booking.department}</p>
                    <p><strong>Date:</strong> {formatDate(booking.date)}</p>
                    <p><strong>Time:</strong> {booking.time_slot}</p>
                    <p><strong>Lab:</strong> {booking.lab}</p>
                    <p><strong>Purpose:</strong> {booking.purpose}</p>
                    <p className={`status status-${booking.status.toLowerCase()}`}>
                      <strong>Status:</strong> {booking.status}
                    </p>
                    <p><strong>Action Date:</strong> {formatDate(booking.action_date)}</p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PendingBookings;
