import React, { useState, useEffect } from 'react';
import { fetchAvailableSeats, bookSlot, fetchBookedSlots, deleteBookedSlot } from '../services/api';
import './SlotBooking.css';

const SlotBooking = () => {
  // Form state
  const [bookingForm, setBookingForm] = useState({
    name: '',
    email: '',
    department: 'CSE',
    date: '',
    timeSlot: ''
  });

  // Delete booking form state
  const [deleteEmail, setDeleteEmail] = useState('');
  
  // UI state
  const [timeSlots, setTimeSlots] = useState([]);
  const [loading, setLoading] = useState(false);
  const [bookedSlots, setBookedSlots] = useState([]);
  const [showBookedSlots, setShowBookedSlots] = useState(false);
  const [error, setError] = useState('');

  // Set minimum date to today
  useEffect(() => {
    const today = new Date().toISOString().split("T")[0];
    document.getElementById("date").setAttribute("min", today);
  }, []);

  // Handle booking form input changes
  const handleBookingChange = (e) => {
    const { name, value } = e.target;
    setBookingForm(prev => ({ ...prev, [name]: value }));

    // Fetch available seats when date changes
    if (name === 'date' && value) {
      handleFetchAvailableSeats(value);
    }
  };

  // Fetch available seats for selected date
  const handleFetchAvailableSeats = async (selectedDate) => {
    if (!selectedDate) {
      alert("Please select a date to check available slots.");
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const data = await fetchAvailableSeats(selectedDate);
      
      // Transform data for the time slot dropdown
      const availableTimeSlots = [
        { value: "9-11", label: "9:00 AM - 11:00 AM", available: data["9-11"] || 0 },
        { value: "11-1", label: "11:00 AM - 1:00 PM", available: data["11-1"] || 0 },
        { value: "2-4", label: "2:00 PM - 4:00 PM", available: data["2-4"] || 0 },
        { value: "4-6", label: "4:00 PM - 6:00 PM", available: data["4-6"] || 0 }
      ];
      
      setTimeSlots(availableTimeSlots);
    } catch (error) {
      setError(`Error fetching available seats: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Handle booking form submission
  const handleBookingSubmit = async (e) => {
    e.preventDefault();
    
    // Validate form inputs
    if (!bookingForm.name || !bookingForm.email || !bookingForm.date || !bookingForm.timeSlot) {
      alert("Please fill out all fields before submitting.");
      return;
    }

    // Validate registration number length
    if (bookingForm.name.length !== 15) {
      alert("Registration number should be exactly 15 characters long.");
      return;
    }

    // Validate SRM email
    if (!bookingForm.email.toLowerCase().endsWith('@srmist.edu.in')) {
      alert("Please use an email ending with @srmist.edu.in");
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const data = await bookSlot(bookingForm);
      alert(data.message);
      
      // Reset form and refresh available seats
      setBookingForm({
        name: '',
        email: '',
        department: 'CSE',
        date: '',
        timeSlot: ''
      });
      
    } catch (error) {
      setError(`Error booking slot: ${error.message}`);
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Handle delete email change
  const handleDeleteEmailChange = (e) => {
    setDeleteEmail(e.target.value);
  };

  // Fetch booked slots for a user
  const handleFetchBookedSlots = async () => {
    if (!deleteEmail) {
      alert("Please enter your email to fetch booked slots.");
      return;
    }

    // Validate SRM email
    if (!deleteEmail.toLowerCase().endsWith('@srmist.edu.in')) {
      alert("Please use an email ending with @srmist.edu.in");
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const data = await fetchBookedSlots(deleteEmail);
      setBookedSlots(data);
      setShowBookedSlots(true);
    } catch (error) {
      setError(`Error fetching booked slots: ${error.message}`);
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Delete a booked slot
  const handleDeleteBookedSlot = async (bookingId) => {
    if (!window.confirm("Are you sure you want to delete this slot?")) {
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const data = await deleteBookedSlot(bookingId);
      alert(data.message);
      
      // Refresh the booked slots
      handleFetchBookedSlots();
    } catch (error) {
      setError(`Error deleting slot: ${error.message}`);
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <main>
      {error && (
        <div className="error-message">
          {error}
        </div>
      )}
      
      <section id="lab-booking">
        <h2>Book Your Lab Slot</h2>
        <form id="labBookingForm" onSubmit={handleBookingSubmit}>
          <label htmlFor="name">Registration Number:</label>
          <input 
            type="text" 
            id="name" 
            name="name" 
            value={bookingForm.name}
            onChange={handleBookingChange}
            required 
            maxLength="15"
            placeholder="Enter your 15-digit registration number"
          />

          <label htmlFor="email">Email (SRM):</label>
          <input 
            type="email" 
            id="email" 
            name="email" 
            value={bookingForm.email}
            onChange={handleBookingChange}
            required 
            placeholder="youremail@srmist.edu.in"
          />

          <label htmlFor="department">Department:</label>
          <select 
            id="department" 
            name="department" 
            value={bookingForm.department}
            onChange={handleBookingChange}
            required
          >
            <option value="CSE">Computer Science and Engineering</option>
            <option value="ME">Mechanical Engineering</option>
            <option value="EEE">Electrical and Electronics Engineering</option>
            <option value="CE">Civil Engineering</option>
            <option value="BT">Biotechnology</option>
          </select>

          <label htmlFor="date">Date:</label>
          <input 
            type="date" 
            id="date" 
            name="date" 
            value={bookingForm.date}
            onChange={handleBookingChange}
            required 
          />

          <label htmlFor="timeSlot">Time Slot:</label>
          <select 
            id="timeSlot" 
            name="timeSlot" 
            value={bookingForm.timeSlot}
            onChange={handleBookingChange}
            required
          >
            {loading && timeSlots.length === 0 ? (
              <option value="">Loading available slots...</option>
            ) : timeSlots.length > 0 ? (
              timeSlots.map(slot => (
                <option 
                  key={slot.value} 
                  value={slot.value}
                  disabled={slot.available === 0}
                >
                  {`${slot.label} (${slot.available} seats available)`}
                </option>
              ))
            ) : (
              <option value="">Select a date first</option>
            )}
          </select>

          <div className="button-container">
            <button 
              type="submit" 
              id="bookSlotButton" 
              disabled={loading}
            >
              {loading ? 'Booking...' : 'Book Slot'}
            </button>
          </div>
        </form>
      </section>

      <section id="delete-booking">
        <h2>Delete Your Booked Slot</h2>
        <form id="deleteBookingForm">
          <label htmlFor="deleteEmail">Email (SRM):</label>
          <input 
            type="email" 
            id="deleteEmail" 
            name="deleteEmail" 
            value={deleteEmail}
            onChange={handleDeleteEmailChange}
            required 
            placeholder="youremail@srmist.edu.in"
          />

          <div className="button-container">
            <button 
              type="button" 
              id="fetchSlotsButton" 
              onClick={handleFetchBookedSlots}
              disabled={loading}
            >
              {loading ? 'Fetching...' : 'Fetch Booked Slots'}
            </button>
          </div>
        </form>

        <div id="bookedSlotsContainer" className={showBookedSlots ? '' : 'hidden'}>
          <h3>Your Booked Slots</h3>
          <ul id="bookedSlotsList">
            {bookedSlots.length === 0 ? (
              <li>No slots booked.</li>
            ) : (
              bookedSlots.map(slot => (
                <li key={slot.id}>
                  Date: {slot.date}, Time Slot: {slot.time_slot}
                  <button 
                    onClick={() => handleDeleteBookedSlot(slot.id)}
                    disabled={loading}
                  >
                    Delete
                  </button>
                </li>
              ))
            )}
          </ul>
        </div>
      </section>
    </main>
  );
};

export default SlotBooking;
