import React, { useState, useEffect } from 'react';
import { fetchAvailableSeats, bookSlot, fetchBookedSlots, deleteBookedSlot, checkApiHealth, API_BASE_URL } from '../services/api';
import './SlotBooking.css';
import { supabase } from '../supabaseClient';
import TimeSlots from '../components/TimeSlots';

const SlotBooking = () => {
  // Form state
  const [bookingForm, setBookingForm] = useState({
    facultyId: '',
    name: '',
    email: '',
    department: '',
    date: '',
    lab: '',
    timeSlot: ''
  });

  // UI state
  const [timeSlots, setTimeSlots] = useState([]);
  const [loading, setLoading] = useState(false);
  const [bookedSlots, setBookedSlots] = useState([]);
  const [showBookedSlots, setShowBookedSlots] = useState(true); // Set to true by default
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [apiError, setApiError] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [message, setMessage] = useState('');

  // Check API health on component mount
  useEffect(() => {
    const checkServerHealth = async () => {
      const isHealthy = await checkApiHealth();
      setApiError(!isHealthy);
    };

    checkServerHealth();

    // Set the minimum date to today
    const today = new Date().toISOString().split("T")[0];
    const dateInput = document.getElementById("date");
    if (dateInput) {
      dateInput.setAttribute("min", today);

      // Add event listener to disable weekends
      dateInput.addEventListener("input", disableWeekends);
    }
  }, []);

  // Function to disable weekend dates
  const disableWeekends = (e) => {
    const date = new Date(e.target.value);
    const day = date.getDay();

    // 0 is Sunday, 6 is Saturday
    if (day === 0 || day === 6) {
      setError("Weekends (Saturday and Sunday) are not available for booking.");

      // Reset the date input
      setBookingForm(prev => ({
        ...prev,
        date: ''
      }));
    }
  };

  useEffect(() => {
    const initializeBookings = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (user) {
          // Set both email and name in the booking form when user is authenticated
          setBookingForm(prev => ({
            ...prev,
            email: user.email,
            name: user.user_metadata?.full_name || user.email
          }));
          // Fetch user bookings if email is available
          if (user.email) {
            await fetchUserBookings(user.email);
          }
        }
      } catch (error) {
        console.error('Error initializing bookings:', error);
      }
    };

    initializeBookings();
  }, []);

  // Utility function to check if a date is a weekend
  const isWeekend = (date) => {
    const day = new Date(date).getDay();
    // 0 is Sunday, 6 is Saturday
    return day === 0 || day === 6;
  };

  // Function to provide a descriptive date string
  const formatDateForDisplay = (dateString) => {
    const date = new Date(dateString);
    const day = date.getDay();
    const isWeekendDay = day === 0 || day === 6;
    const formattedDate = date.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });

    return `${formattedDate}${isWeekendDay ? ' (Weekend - Not Available)' : ''}`;
  };

  // Handle booking form input changes
  const handleBookingChange = (e) => {
    const { name, value } = e.target;

    // Prevent only email from being changed
    if (name === 'email') {
      return;
    }

    // Check if it's a weekend date
    if (name === 'date' && value) {
      if (isWeekend(value)) {
        setError("Weekends (Saturday and Sunday) are not available for booking.");
        return;
      }
    }

    // Update the booking form state
    setBookingForm(prev => ({ ...prev, [name]: value }));

    // Clear previous errors and success messages
    setError('');
    setSuccess('');

    // Fetch available seats when date changes
    if (name === 'date' && value) {
      setTimeSlots([]);
      if (bookingForm.lab) {
        handleFetchAvailableSeats(value, bookingForm.lab);
      }
    }

    // Fetch available seats when lab changes
    if (name === 'lab' && value) {
      setTimeSlots([]);
      if (bookingForm.date) {
        handleFetchAvailableSeats(bookingForm.date, value);
      }
    }
  };

  // Retry server connection
  const handleRetryConnection = async () => {
    setLoading(true);
    const isHealthy = await checkApiHealth();
    setApiError(!isHealthy);
    setLoading(false);

    if (isHealthy && bookingForm.date && bookingForm.lab) {
      handleFetchAvailableSeats(bookingForm.date, bookingForm.lab);
    }
  };

  // Fetch available seats for selected date and lab
  const handleFetchAvailableSeats = async (selectedDate, selectedLab) => {
    if (!selectedDate || !selectedLab) {
      setTimeSlots([]);
      return;
    }

    setLoading(true);
    setError('');
    setApiError(false);

    try {
      const data = await fetchAvailableSeats(selectedDate, selectedLab);
      console.log('Available slots response:', data);

      // Transform data for the time slot dropdown with boolean availability
      const availableTimeSlots = [
        { value: "08:00-08:50", label: "8:00 AM - 8:50 AM", available: data["08:00-08:50"] === true },
        { value: "08:50-09:40", label: "8:50 AM - 9:40 AM", available: data["08:50-09:40"] === true },
        { value: "09:45-10:35", label: "9:45 AM - 10:35 AM", available: data["09:45-10:35"] === true },
        { value: "10:40-11:30", label: "10:40 AM - 11:30 AM", available: data["10:40-11:30"] === true },
        { value: "11:35-12:25", label: "11:35 AM - 12:25 PM", available: data["11:35-12:25"] === true },
        { value: "12:30-01:20", label: "12:30 PM - 1:20 PM", available: data["12:30-01:20"] === true },
        { value: "01:25-02:15", label: "1:25 PM - 2:15 PM", available: data["01:25-02:15"] === true },
        { value: "02:20-03:10", label: "2:20 PM - 3:10 PM", available: data["02:20-03:10"] === true },
        { value: "03:10-04:00", label: "3:10 PM - 4:00 PM", available: data["03:10-04:00"] === true },
        { value: "04:00-04:50", label: "4:00 PM - 4:50 PM", available: data["04:00-04:50"] === true }
      ];

      setTimeSlots(availableTimeSlots);
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle booking form submission
  const handleBookingSubmit = async (e) => {
    e.preventDefault();

    // Clear previous messages
    setError('');
    setSuccess('');

    // Validate form inputs
    if (!bookingForm.facultyId || !bookingForm.name || !bookingForm.email || !bookingForm.date || !bookingForm.timeSlot) {
      setError("Please fill out all fields before submitting.");
      return;
    }

    // Validate SRM email
    if (!bookingForm.email.toLowerCase().endsWith('@srmist.edu.in')) {
      setError("Please use an email ending with @srmist.edu.in");
      return;
    }

    setLoading(true);

    try {
      const data = await bookSlot(bookingForm);
      setSuccess(data.message);

      // Refresh the bookings after successful booking
      fetchUserBookings(bookingForm.email);

      // Reset form only on successful booking
      setBookingForm({
        facultyId: '',
        name: '',
        email: bookingForm.email, // Keep the email
        department: '',
        date: '',
        lab: '',
        timeSlot: ''
      });

      // Clear time slots
      setTimeSlots([]);

    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  // Fetch booked slots for a user
  const fetchUserBookings = async (email) => {
    if (!email || !email.toLowerCase().endsWith('@srmist.edu.in')) {
      return;
    }

    setLoading(true);

    try {
      const data = await fetchBookedSlots(email);
      setBookedSlots(data);
      setShowBookedSlots(true);

      if (data.length === 0) {
        setSuccess("No booked slots found for your email.");
      }
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        console.error('Error fetching bookings:', error);
      }
    } finally {
      setLoading(false);
    }
  };

  // Delete a booked slot
  const handleDeleteBookedSlot = async (bookingId) => {
    if (!window.confirm("Are you sure you want to delete this slot?")) {
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const data = await deleteBookedSlot(bookingId);
      setSuccess(data.message);

      // Refresh the booked slots using the email from the form
      if (bookingForm.email) {
        fetchUserBookings(bookingForm.email);
      }

      // Fetch available seats again to update the slots
      if (bookingForm.date && bookingForm.lab) {
        handleFetchAvailableSeats(bookingForm.date, bookingForm.lab);
      }

    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
    setSelectedSlot(null);
    setError('');
    setSuccess('');
  };

  const handleSlotSelect = (slot) => {
    setSelectedSlot(slot);
    setError('');
    setSuccess('');
  };

  const handleBooking = async () => {
    if (!selectedDate || !selectedSlot) {
      setError('Please select both date and time slot.');
      return;
    }

    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        setError('Please sign in to book a slot.');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/book-slot`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          date: selectedDate,
          timeSlot: selectedSlot,
          email: user.email,
          facultyId: user.user_metadata?.registration_number || user.email,
          name: user.user_metadata?.full_name || user.email,
          department: user.user_metadata?.department || 'Not specified'
        }),
      });

      // Check if the response is JSON
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        console.error('Non-JSON response:', await response.text());
        throw new Error('Backend server error. Please make sure the server is running.');
      }

      const data = await response.json();

      if (!response.ok) {
        setError(data.message || 'Error booking slot.');
        return;
      }

      setSuccess(data.message);

      // Reset selections after successful booking
      setSelectedSlot(null);
      // Refresh the bookings list
      fetchUserBookings(user.email);
    } catch (error) {
      setError('Error booking slot. Please try again.');
      console.error('Booking error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <main>
      {apiError && (
        <div className="api-error">
          <h3>Connection Error</h3>
          <p>Cannot connect to the backend server. Please make sure the Flask server is running on port 8080.</p>
          <button 
            onClick={handleRetryConnection} 
            disabled={loading}
            className="action-button"
          >
            {loading ? 'Trying to connect...' : 'Retry Connection'}
          </button>
        </div>
      )}

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {success && (
        <div className="success-message">
          {success}
        </div>
      )}

      {message && (
        <div className={`message ${message.includes('Error') || message.includes('Please') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      <section id="lab-booking">
        <h2>Book Your Lab Slot</h2>
        <form onSubmit={handleBookingSubmit}>
          <label htmlFor="facultyId">Faculty ID:</label>
          <input 
            type="text" 
            id="facultyId" 
            name="facultyId" 
            value={bookingForm.facultyId}
            onChange={handleBookingChange}
            required 
            placeholder="Enter your faculty ID"
            disabled={loading || apiError}
          />

          <label htmlFor="name">Full Name:</label>
          <input 
            type="text" 
            id="name" 
            name="name" 
            value={bookingForm.name}
            onChange={handleBookingChange}
            required
            placeholder="Enter your full name"
          />

          <label htmlFor="email">Email (SRM):</label>
          <input 
            type="email" 
            id="email" 
            name="email" 
            value={bookingForm.email}
            readOnly
            disabled
            className="readonly-input"
          />

          <label htmlFor="department">Department:</label>
          <select 
            id="department" 
            name="department" 
            value={bookingForm.department}
            onChange={handleBookingChange}
            required
            disabled={loading || apiError}
          >
            <option value="">Select Department</option>
            <option value="NETWORKING">Networking</option>
            <option value="CINTEL">CINTEL</option>
            <option value="DSBS">DSBS</option>
            <option value="CTECH">CTECH</option>
          </select>

          <label htmlFor="date">Date:</label>
          <input 
            type="date" 
            id="date" 
            name="date" 
            value={bookingForm.date}
            onChange={handleBookingChange}
            required 
            disabled={loading || apiError}
            min={new Date().toISOString().split("T")[0]}
            onKeyDown={(e) => e.preventDefault()}
          />
          <small className="date-instructions">Note: Weekends (Saturday and Sunday) are not available for booking.</small>

          <label htmlFor="lab">Select Lab:</label>
          <select 
            id="lab" 
            name="lab" 
            value={bookingForm.lab}
            onChange={handleBookingChange}
            required
            disabled={loading || apiError}
            className="lab-select"
          >
            <option value="">Select a Lab</option>
            <option value="LAB A">LAB A</option>
            <option value="LAB B">LAB B</option>
            <option value="LAB C">LAB C</option>
            <option value="LAB D">LAB D</option>
          </select>

          <label htmlFor="timeSlot">Time Slot:</label>
          <select 
            id="timeSlot" 
            name="timeSlot" 
            value={bookingForm.timeSlot}
            onChange={handleBookingChange}
            required
            disabled={loading || apiError || timeSlots.length === 0}
            className="time-slot-select"
          >
            <option value="">Select a slot</option>
            {loading && timeSlots.length === 0 ? (
              <option value="">Loading available slots...</option>
            ) : timeSlots.length > 0 ? (
              timeSlots.map(slot => (
                <option 
                  key={slot.value} 
                  value={slot.value}
                  disabled={!slot.available}
                  className={!slot.available ? 'slot-full' : ''}
                >
                  {`${slot.label} (${slot.available ? 'Available' : 'Booked'})`}
                </option>
              ))
            ) : (
              <option value="">Select a date first</option>
            )}
          </select>

          <div className="button-container">
            <button 
              type="submit" 
              id="bookSlotButton" 
              className="booking-button"
              disabled={loading || apiError}
            >
              {loading ? 'Booking...' : 'Book Slot'}
            </button>
          </div>
        </form>
      </section>

      <section id="user-bookings">
        <h2>Your Booked Slots</h2>

        {loading && <div className="loading">Loading your bookings...</div>}

        {bookedSlots.length > 0 ? (
          <div className="booked-slots-list">
            {bookedSlots.map(slot => (
              <div key={slot.id} className="booked-slot-item">
                <div className="booked-slot-details">
                  <strong>Date:</strong> {slot.date}<br />
                  <strong>Time:</strong> {slot.time_slot}<br />
                  <strong>Lab:</strong> {slot.lab || "Not specified"}
                </div>
                <button 
                  onClick={() => handleDeleteBookedSlot(slot.id)}
                  disabled={loading || apiError}
                  className="delete-button"
                >
                  Cancel Booking
                </button>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-bookings">
            {loading ? 'Loading...' : 'No bookings found for your account.'}
          </div>
        )}
      </section>
    </main>
  );
};

export default SlotBooking;
