import React, { useState, useEffect } from 'react';
import { fetchAvailableSeats, bookSlot, fetchBookedSlots, deleteBookedSlot, checkApiHealth, API_BASE_URL } from '../services/api';
import './SlotBooking.css';
import { supabase } from '../supabaseClient';
import TimeSlots from '../components/TimeSlots';

const SlotBooking = () => {
  // Form state
  const [bookingForm, setBookingForm] = useState({
    facultyId: '',
    name: '',
    email: '',
    department: '',
    date: '',
    timeSlot: ''
  });

  // Delete booking form state
  const [deleteEmail, setDeleteEmail] = useState('');
  
  // UI state
  const [timeSlots, setTimeSlots] = useState([]);
  const [loading, setLoading] = useState(false);
  const [bookedSlots, setBookedSlots] = useState([]);
  const [showBookedSlots, setShowBookedSlots] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [apiError, setApiError] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [message, setMessage] = useState('');

  // Check API health on component mount
  useEffect(() => {
    const checkServerHealth = async () => {
      const isHealthy = await checkApiHealth();
      setApiError(!isHealthy);
    };
    
    checkServerHealth();
    
    // Set the minimum date to today
    const today = new Date().toISOString().split("T")[0];
    document.getElementById("date").setAttribute("min", today);
  }, []);

  useEffect(() => {
    const initializeBookings = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (user) {
          // Set both email and name in the booking form when user is authenticated
          setBookingForm(prev => ({
            ...prev,
            email: user.email,
            name: user.user_metadata?.full_name || user.email
          }));
          await fetchUserBookings();
        }
      } catch (error) {
        console.error('Error initializing bookings:', error);
      }
    };

    initializeBookings();
  }, []);

  // Handle booking form input changes
  const handleBookingChange = (e) => {
    const { name, value } = e.target;
    
    // Prevent only email from being changed
    if (name === 'email') {
      return;
    }
    
    // Update the booking form state
    setBookingForm(prev => ({ ...prev, [name]: value }));

    // Clear previous errors and success messages
    setError('');
    setSuccess('');

    // Fetch available seats when date changes
    if (name === 'date' && value) {
      handleFetchAvailableSeats(value);
    }
  };

  // Retry server connection
  const handleRetryConnection = async () => {
    setLoading(true);
    const isHealthy = await checkApiHealth();
    setApiError(!isHealthy);
    setLoading(false);
    
    if (isHealthy && bookingForm.date) {
      handleFetchAvailableSeats(bookingForm.date);
    }
  };

  // Fetch available seats for selected date
  const handleFetchAvailableSeats = async (selectedDate) => {
    if (!selectedDate) {
      alert("Please select a date to check available slots.");
      return;
    }

    setLoading(true);
    setError('');
    setApiError(false);
    
    try {
      const data = await fetchAvailableSeats(selectedDate);
      
      // Transform data for the time slot dropdown with availability status only
      const availableTimeSlots = [
        { value: "08:00-08:50", label: "8:00 AM - 8:50 AM", available: data["08:00-08:50"] > 0 },
        { value: "08:50-09:40", label: "8:50 AM - 9:40 AM", available: data["08:50-09:40"] > 0 },
        { value: "09:45-10:35", label: "9:45 AM - 10:35 AM", available: data["09:45-10:35"] > 0 },
        { value: "10:40-11:30", label: "10:40 AM - 11:30 AM", available: data["10:40-11:30"] > 0 },
        { value: "11:35-12:25", label: "11:35 AM - 12:25 PM", available: data["11:35-12:25"] > 0 },
        { value: "12:30-01:20", label: "12:30 PM - 1:20 PM", available: data["12:30-01:20"] > 0 },
        { value: "01:25-02:15", label: "1:25 PM - 2:15 PM", available: data["01:25-02:15"] > 0 },
        { value: "02:20-03:10", label: "2:20 PM - 3:10 PM", available: data["02:20-03:10"] > 0 },
        { value: "03:10-04:00", label: "3:10 PM - 4:00 PM", available: data["03:10-04:00"] > 0 },
        { value: "04:00-04:50", label: "4:00 PM - 4:50 PM", available: data["04:00-04:50"] > 0 }
      ];
      
      setTimeSlots(availableTimeSlots);
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle booking form submission
  const handleBookingSubmit = async (e) => {
    e.preventDefault();
    
    // Clear previous messages
    setError('');
    setSuccess('');
    
    // Validate form inputs
    if (!bookingForm.facultyId || !bookingForm.name || !bookingForm.email || !bookingForm.date || !bookingForm.timeSlot) {
      setError("Please fill out all fields before submitting.");
      return;
    }

    // Validate SRM email
    if (!bookingForm.email.toLowerCase().endsWith('@srmist.edu.in')) {
      setError("Please use an email ending with @srmist.edu.in");
      return;
    }

    setLoading(true);
    
    try {
      const data = await bookSlot(bookingForm);
      setSuccess(data.message);
      
      // Reset form only on successful booking
      setBookingForm({
        facultyId: '',
        name: '',
        email: '',
        department: '',
        date: '',
        timeSlot: ''
      });
      
      // Clear time slots
      setTimeSlots([]);
      
      // Fetch available seats again to update the slots
      handleFetchAvailableSeats(bookingForm.date);
      
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle delete email change
  const handleDeleteEmailChange = (e) => {
    setDeleteEmail(e.target.value);
    setError('');
    setSuccess('');
  };

  // Fetch booked slots for a user
  const handleFetchBookedSlots = async () => {
    // Clear previous messages
    setError('');
    setSuccess('');
    
    if (!deleteEmail) {
      setError("Please enter your email to fetch booked slots.");
      return;
    }

    // Validate SRM email
    if (!deleteEmail.toLowerCase().endsWith('@srmist.edu.in')) {
      setError("Please use an email ending with @srmist.edu.in");
      return;
    }

    setLoading(true);
    
    try {
      const data = await fetchBookedSlots(deleteEmail);
      setBookedSlots(data);
      setShowBookedSlots(true);
      
      if (data.length === 0) {
        setSuccess("No booked slots found for this email.");
      }
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
      setShowBookedSlots(false);
    } finally {
      setLoading(false);
    }
  };

  // Delete a booked slot
  const handleDeleteBookedSlot = async (bookingId) => {
    if (!window.confirm("Are you sure you want to delete this slot?")) {
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');
    
    try {
      const data = await deleteBookedSlot(bookingId);
      setSuccess(data.message);
      
      // Refresh the booked slots
      handleFetchBookedSlots();
      
      // Fetch available seats again to update the slots
      if (bookingForm.date) {
        handleFetchAvailableSeats(bookingForm.date);
      }
      
    } catch (error) {
      if (error.message.includes('Backend server is not running')) {
        setApiError(true);
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
    setSelectedSlot(null);
    setError('');
    setSuccess('');
  };

  const handleSlotSelect = (slot) => {
    setSelectedSlot(slot);
    setError('');
    setSuccess('');
  };

  const handleBooking = async () => {
    if (!selectedDate || !selectedSlot) {
      setError('Please select both date and time slot.');
      return;
    }

    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        setError('Please sign in to book a slot.');
        return;
      }

      const response = await fetch(`${API_BASE_URL}/book-slot`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          date: selectedDate,
          timeSlot: selectedSlot,
          email: user.email,
          facultyId: user.user_metadata?.registration_number || user.email,
          name: user.user_metadata?.full_name || user.email,
          department: user.user_metadata?.department || 'Not specified'
        }),
      });

      // Check if the response is JSON
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        console.error('Non-JSON response:', await response.text());
        throw new Error('Backend server error. Please make sure the server is running.');
      }

      const data = await response.json();
      
      if (!response.ok) {
        setError(data.message || 'Error booking slot.');
        return;
      }
      
      setSuccess(data.message);
      
      // Reset selections after successful booking
      setSelectedSlot(null);
      // Refresh the bookings list
      fetchUserBookings();
    } catch (error) {
      setError('Error booking slot. Please try again.');
      console.error('Booking error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserBookings = async () => {
    try {
      setLoading(true);
      setMessage('');
      
      const { data: { user }, error: authError } = await supabase.auth.getUser();
      
      if (authError) {
        console.error('Auth error:', authError);
        setMessage('Authentication error. Please sign in again.');
        return;
      }
      
      if (!user) {
        setMessage('Please sign in to view your bookings.');
        return;
      }

      console.log('Fetching bookings for:', user.email);
      const response = await fetch(`${API_BASE_URL}/booked-slots?email=${encodeURIComponent(user.email)}`);
      
      // Check if the response is JSON
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        console.error('Non-JSON response:', await response.text());
        throw new Error('Backend server error. Please make sure the server is running.');
      }
      
      const data = await response.json();
      
      if (!response.ok) {
        console.error('Server error:', data);
        throw new Error(data.error || 'Failed to fetch bookings');
      }
      
      console.log('Fetched bookings:', data);
      
      setBookedSlots(data);
      setShowBookedSlots(true);
      
      if (data.length === 0) {
        setMessage('No bookings found.');
      }
    } catch (error) {
      console.error('Error in fetchUserBookings:', error);
      if (error.message.includes('Failed to fetch') || error.message.includes('Backend server error')) {
        setApiError(true);
      } else {
        setMessage(error.message || 'Error fetching your bookings.');
      }
      setShowBookedSlots(false);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to delete this booking?')) {
      return;
    }

    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/delete-slot/${bookingId}`, {
        method: 'DELETE'
      });

      const data = await response.json();
      
      if (response.ok) {
        setMessage('Booking deleted successfully!');
        // Refresh the bookings list
        fetchUserBookings();
      } else {
        setMessage(data.error || 'Error deleting booking.');
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('Error deleting booking.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <main>
      {apiError && (
        <div className="api-error">
          <h3>Connection Error</h3>
          <p>Cannot connect to the backend server. Please make sure the Flask server is running on port 8080.</p>
          <button 
            onClick={handleRetryConnection} 
            disabled={loading}
            className="action-button"
          >
            {loading ? 'Trying to connect...' : 'Retry Connection'}
          </button>
        </div>
      )}
      
      {error && (
        <div className="error-message">
          {error}
        </div>
      )}
      
      {success && (
        <div className="success-message">
          {success}
        </div>
      )}
      
      {message && (
        <div className={`message ${message.includes('Error') || message.includes('Please') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}
      
      <section id="lab-booking">
        <h2>Book Your Lab Slot</h2>
        <form onSubmit={handleBookingSubmit}>
          <label htmlFor="facultyId">Faculty ID:</label>
          <input 
            type="text" 
            id="facultyId" 
            name="facultyId" 
            value={bookingForm.facultyId}
            onChange={handleBookingChange}
            required 
            placeholder="Enter your faculty ID"
            disabled={loading || apiError}
          />

          <label htmlFor="name">Full Name:</label>
          <input 
            type="text" 
            id="name" 
            name="name" 
            value={bookingForm.name}
            onChange={handleBookingChange}
            required
            placeholder="Enter your full name"
          />

          <label htmlFor="email">Email (SRM):</label>
          <input 
            type="email" 
            id="email" 
            name="email" 
            value={bookingForm.email}
            readOnly
            disabled
            className="readonly-input"
          />

          <label htmlFor="department">Department:</label>
          <select 
            id="department" 
            name="department" 
            value={bookingForm.department}
            onChange={handleBookingChange}
            required
            disabled={loading || apiError}
          >
            <option value="">Select Department</option>
            <option value="NETWORKING">Networking</option>
            <option value="CINTEL">CINTEL</option>
            <option value="DSBS">DSBS</option>
            <option value="CTECH">CTECH</option>
          </select>

          <label htmlFor="date">Date:</label>
          <input 
            type="date" 
            id="date" 
            name="date" 
            value={bookingForm.date}
            onChange={handleBookingChange}
            required 
            disabled={loading || apiError}
          />

          <label htmlFor="timeSlot">Time Slot:</label>
          <select 
            id="timeSlot" 
            name="timeSlot" 
            value={bookingForm.timeSlot}
            onChange={handleBookingChange}
            required
            disabled={loading || apiError || timeSlots.length === 0}
            className="time-slot-select"
          >
            <option value="">Select a slot</option>
            {loading && timeSlots.length === 0 ? (
              <option value="">Loading available slots...</option>
            ) : timeSlots.length > 0 ? (
              timeSlots.map(slot => (
                <option 
                  key={slot.value} 
                  value={slot.value}
                  disabled={!slot.available}
                  className={!slot.available ? 'slot-full' : ''}
                >
                  {`${slot.label} (${slot.available ? 'Available' : 'Booked'})`}
                </option>
              ))
            ) : (
              <option value="">Select a date first</option>
            )}
          </select>

          <div className="button-container">
            <button 
              type="submit" 
              id="bookSlotButton" 
              className="booking-button"
              disabled={loading || apiError}
            >
              {loading ? 'Booking...' : 'Book Slot'}
            </button>
          </div>
        </form>
      </section>

      <section id="delete-booking">
        <h2>Delete Your Booked Slot</h2>
        <form id="deleteBookingForm">
          <label htmlFor="deleteEmail">Email (SRM):</label>
          <input 
            type="email" 
            id="deleteEmail" 
            name="deleteEmail" 
            value={deleteEmail}
            onChange={handleDeleteEmailChange}
            required 
            placeholder="youremail@srmist.edu.in"
            disabled={loading || apiError}
            pattern=".*@srmist\.edu\.in"
            title="Please use an email ending with @srmist.edu.in"
          />

          <div className="button-container">
            <button 
              type="button" 
              id="fetchSlotsButton" 
              onClick={handleFetchBookedSlots}
              disabled={loading || apiError || !deleteEmail}
              className="action-button"
            >
              {loading ? 'Fetching...' : 'Fetch Booked Slots'}
            </button>
          </div>
        </form>

        <div id="bookedSlotsContainer" className={showBookedSlots ? '' : 'hidden'}>
          <h3>Your Booked Slots</h3>
          <ul id="bookedSlotsList">
            {bookedSlots.length === 0 ? (
              <li>No slots booked.</li>
            ) : (
              bookedSlots.map(slot => (
                <li key={slot.id}>
                  Date: {slot.date}, Time Slot: {slot.time_slot}
                  <button 
                    onClick={() => handleDeleteBooking(slot.id)}
                    disabled={loading || apiError}
                    className="delete-button"
                  >
                    Delete
                  </button>
                </li>
              ))
            )}
          </ul>
        </div>
      </section>
    </main>
  );
};

export default SlotBooking;
