import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_BASE_URL } from '../services/api';
import { supabase } from '../supabaseClient';
import './DayOrderManagement.css';

const DayOrderManagement = () => {
  const [dayOrders, setDayOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [selectedDate, setSelectedDate] = useState('');
  const [newDayOrder, setNewDayOrder] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    checkAdminAndFetchDayOrders();
  }, []);

  const checkAdminAndFetchDayOrders = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        navigate('/login');
        return;
      }

      const adminEmails = ['km5260@srmist.edu.in', 'manorant@srmist.edu.in'];
      if (!adminEmails.includes(user.email)) {
        navigate('/');
        return;
      }

      await fetchDayOrders(user.email);
    } catch (error) {
      console.error('Error:', error);
      setError('Failed to authenticate');
    }
  };

  const fetchDayOrders = async (adminEmail) => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/admin/day-orders?admin_email=${encodeURIComponent(adminEmail)}`);
      if (!response.ok) throw new Error('Failed to fetch day orders');
      
      const data = await response.json();
      setDayOrders(data);
    } catch (error) {
      console.error('Error fetching day orders:', error);
      setError('Failed to fetch day orders');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateDayOrder = async (e) => {
    e.preventDefault();
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/admin/update-day-order`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          admin_email: user.email,
          date: selectedDate,
          new_day_order: parseInt(newDayOrder)
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || data.message || 'Failed to update day order');
      }

      setSuccess(`Day order updated successfully. ${data.affected_bookings} booking(s) affected.`);
      
      // Clear form
      setSelectedDate('');
      setNewDayOrder('');
      
      // Refresh day orders
      await fetchDayOrders(user.email);
      
      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(null), 3000);
    } catch (error) {
      console.error('Error updating day order:', error);
      setError(error.message || 'Failed to update day order');
    } finally {
      setLoading(false);
    }
  };

  if (loading && !dayOrders.length) return <div className="loading">Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  // Get today's date in YYYY-MM-DD format
  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="day-order-management">
      <h2>Day Order Management</h2>
      {success && <div className="success-message">{success}</div>}
      
      <div className="update-day-order-form">
        <h3>Update Day Order</h3>
        <form onSubmit={handleUpdateDayOrder}>
          <div className="form-group">
            <label htmlFor="date">Select Date:</label>
            <input
              type="date"
              id="date"
              min={today}
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="dayOrder">New Day Order:</label>
            <select
              id="dayOrder"
              value={newDayOrder}
              onChange={(e) => setNewDayOrder(e.target.value)}
              required
            >
              <option value="">Select Day Order</option>
              <option value="1">Day 1</option>
              <option value="2">Day 2</option>
              <option value="3">Day 3</option>
              <option value="4">Day 4</option>
              <option value="5">Day 5</option>
            </select>
          </div>
          
          <button type="submit" disabled={loading}>
            Update Day Order
          </button>
        </form>
      </div>

      <div className="day-orders-list">
        <h3>Upcoming Day Orders</h3>
        <div className="day-orders-grid">
          {dayOrders.map((dayOrder) => (
            <div key={dayOrder.date} className="day-order-card">
              <div className="date">
                {new Date(dayOrder.date).toLocaleDateString('en-US', {
                  weekday: 'long',
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric'
                })}
              </div>
              <div className="day-order">Day {dayOrder.day_order}</div>
              <div className="booking-count">
                {dayOrder.booking_count} booking{dayOrder.booking_count !== 1 ? 's' : ''}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DayOrderManagement;
